package com.microsoft.coginitiveservices.speech.samples.sdsdkstarterapp;import android.content.Intent;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.view.WindowManager;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import java.util.ArrayList;public class subcriptionKey extends AppCompatActivity {    private Button okButton;    private Button resetButton;    private Button returnButton;    private EditText speechKeyEditText;    private EditText speechRegionEditText;    private EditText LuisKeyEditText;    private EditText LuisRegionEditText;    private EditText LuisAppIdEditText;    private TextView warningTextView;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_subcription_key);        okButton = findViewById(R.id.okButton);        resetButton = findViewById(R.id.resetButton);        returnButton = findViewById(R.id.returnButton);        speechKeyEditText = findViewById(R.id.speechKeyEditText);        speechRegionEditText = findViewById(R.id.speechRegionEditText);        LuisKeyEditText = findViewById(R.id.luisKeyEditText);        LuisRegionEditText = findViewById(R.id.luisRegionEditText);        LuisAppIdEditText = findViewById(R.id.luisAppIdEditText);        warningTextView = findViewById(R.id.warningTextView);        warningTextView.setText("");        speechKeyEditText.setText(MainActivity.getSpeechSubscriptionKey());        speechRegionEditText.setText(MainActivity.getSpeechRegion());        LuisKeyEditText.setText(MainActivity.getLuisSubscriptionKey());        LuisRegionEditText.setText(MainActivity.getLuisRegion());        LuisAppIdEditText.setText(MainActivity.getLuisAppId());        getWindow().setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_STATE_ALWAYS_HIDDEN);        okButton.setOnClickListener(view->{            warningTextView.setText("");            ArrayList<String> keys = new ArrayList<>();            if(speechKeyEditText.getText().length()==0){                warningTextView.setText("Please input speech key");                return;            }else{                keys.add(speechKeyEditText.getText().toString());            }            if(speechRegionEditText.getText().toString().length()==0){                warningTextView.setText(warningTextView.getText()+ "  Please check speech region. ");                return;            }else{                keys.add(speechRegionEditText.getText().toString());            }            if(LuisKeyEditText.getText().toString().length()==0){                warningTextView.setText(warningTextView.getText()+ " Please check Luis key");            }            keys.add(LuisKeyEditText.getText().toString());            if(LuisRegionEditText.getText().toString().length()==0){                warningTextView.setText(warningTextView.getText()+ "  Please check Luis region");            }            keys.add(LuisRegionEditText.getText().toString());            if(LuisAppIdEditText.getText().toString().length()==0){                warningTextView.setText(warningTextView.getText()+ "  Please check Luis AppId");            }            keys.add(LuisAppIdEditText.getText().toString());            Intent sendIntent = new Intent();            sendIntent.putStringArrayListExtra("keys",keys);            setResult(RESULT_OK, sendIntent);            finish();        });        resetButton.setOnClickListener(view ->{            speechKeyEditText.setText("");            speechRegionEditText.setText("");            LuisKeyEditText.setText("");            LuisRegionEditText.setText("");            LuisAppIdEditText.setText("");        });        returnButton.setOnClickListener(view->{            setResult(RESULT_CANCELED);            finish();        });    }}